import React, { useState, useEffect } from 'react';

import {
  Container,
  PlayerContainer,
  FigureIcon,
  SVGIcon,
  Timer,
} from './themeStyles';
import MeditatorIcon from '../../images/figure-2.svg';
import { Title, FlexColDiv } from '../../style/style';
import Player from '../../hooks/useAudio';

const svgConfig = {
  width: 453,
  height: 453,
  viewBox: '0 0 453 453',
  fill: 'none',
  xmlns: 'http://www.w3.org/2000/svg',
};

const circleConfig = {
  cx: 226.5,
  cy: 226.5,
  r: 216.5,
};

function Theme(props) {
  const { url } = props;

  // const [duration, setDuration] = useState(300);
  // const timer = `${Math.floor(duration / 60)}: ${Math.floor(duration % 60)}`;
  const [seconds, setSeconds] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [start, setStart] = useState(false);

  function updateTime() {
    if (start) {
      if (seconds === 0) {
        setMinutes(minutes => minutes - 1);
        setSeconds(59);
      } else {
        setSeconds(seconds => seconds - 1);
      }
    }
  }

  useEffect(() => {
    if (start) {
      const token = setTimeout(updateTime, 1000);

      return function cleanUp() {
        clearTimeout(token);
      };
    }
  });

  // Choose time (5min)
  // Display chosen time
  // Click play button
  // Play sound and display left time

  const handleClick = time => {
    setMinutes(time);
    setSeconds(0);

    if (start) setStart(false);
  };

  const handleToggle = () => {
    setStart(!start);
  };

  return (
    <Container>
      <Title size="md">No one save us but ourselves</Title>

      <PlayerContainer>
        <FigureIcon src={MeditatorIcon} alt="meditator" />

        <SVGIcon
          // class="track-outline"
          {...svgConfig}>
          <circle {...circleConfig} stroke="white" strokeWidth="20" />
        </SVGIcon>
        <SVGIcon
          // class="moving-outline"
          {...svgConfig}>
          <circle {...circleConfig} stroke="#018EBA" strokeWidth="20" />
        </SVGIcon>
      </PlayerContainer>

      <FlexColDiv>
        {/* <Timer size="sm">0:00</Timer> */}
        <Timer size="sm">
          {Math.floor(minutes / 60)}:{Math.floor(seconds % 60)}
        </Timer>
        <Player url={url} setStart={handleToggle} />
        {/* <button onClick={() => setMinutes(300)}>5 min</button>
        <button onClick={() => setMinutes(600)}>10 min</button>
        <button onClick={() => setMinutes(900)}>15 min</button> */}
        <button onClick={() => handleClick(300)}>5 min</button>
        <button onClick={() => handleClick(600)}>10 min</button>
        <button onClick={() => handleClick(900)}>15 min</button>
      </FlexColDiv>
      {props.children}
    </Container>
  );
}

export default Theme;
