import React, { useState, useEffect } from 'react';

import {
  Container,
  PlayerContainer,
  FigureIcon,
  SVGIcon,
  Timer,
} from './themeStyles';
import MeditatorIcon from '../../images/figure-2.svg';
import { Title, FlexColDiv } from '../../style/style';
import Player from '../../hooks/useAudio';
import TimeLoader from '../timeLoader/timeLoader';

// const svgConfig = {
//   width: 453,
//   height: 453,
//   viewBox: '0 0 453 453',
//   fill: 'none',
//   xmlns: 'http://www.w3.org/2000/svg',
// };

// const circleConfig = {
//   cx: 226.5,
//   cy: 226.5,
//   r: 216.5,
// };

function Theme(props) {
  const { url } = props;

  const [start, setStart] = useState(false);
  const [duration, setDuration] = useState({ min: 600, sec: 0 });
  const { min, sec } = duration;

  const displayTime = `${Math.floor(min / 60)}:${Math.floor(sec % 60)}`;

  const handleClick = time => {
    setDuration({ min: time, sec: 0 });
  };

  const handleToggle = () => {
    setStart(!start);
  };

  const updateTime = () => {
    if (start) {
      if (sec === 0) {
        setDuration({
          min: min - 1,
          sec: 59,
        });
      } else {
        setDuration({ ...duration, sec: sec - 1 });
      }
    }
  };

  useEffect(() => {
    if (start) {
      const token = setTimeout(updateTime, 1000);

      return function cleanUp() {
        clearTimeout(token);
      };
    }
  });

  return (
    <Container>
      <Title size="md">No one save us but ourselves</Title>

      {/* <PlayerContainer>
        <FigureIcon src={MeditatorIcon} alt="meditator" />

        <SVGIcon
          // class="track-outline"
          {...svgConfig}>
          <circle {...circleConfig} stroke="white" strokeWidth="20" />
        </SVGIcon>
        <SVGIcon
          // class="moving-outline"
          {...svgConfig}>
          <circle {...circleConfig} stroke="#018EBA" strokeWidth="20" />
        </SVGIcon>
      </PlayerContainer> */}
      <TimeLoader />

      <FlexColDiv>
        <Timer size="sm">{displayTime}</Timer>
        <Player url={url} setStart={handleToggle} />
        <button onClick={() => handleClick(300)}>5 min</button>
        <button onClick={() => handleClick(600)}>10 min</button>
        <button onClick={() => handleClick(900)}>15 min</button>
      </FlexColDiv>
      {props.children}
    </Container>
  );
}

export default Theme;
