        <circle
          // {...circleConfig}
          cx="226.5"
          cy="226.5"
          r="216.5"
          stroke="#018EBA"
          strokeWidth="20"
          ref={circleRef}
        />
      </SVGIcon> */}
      <svg
        className="track-outline"
        width="453"
        height="453"
        viewBox="0 0 453 453"
        fill="none"
        xmlns="http://www.w3.org/2000/svg">
        <circle
          cx="226.5"
          cy="226.5"
          r="216.5"
          stroke="white"
          stroke-width="20"
        />
      </svg>
      <svg
        className="moving-outline"
        width="453"
        height="453"
        viewBox="0 0 453 453"
        fill="none"
        xmlns="http://www.w3.org/2000/svg">
        <circle
          cx="226.5"
          cy="226.5"
          r="216.5"
          stroke="#018EBA"
          stroke-width="20"
          ref={ref}
        />
      </svg>
    </PlayerContainer>
  );
}

export default TimeLoader;
