import React from 'react';

import { Title, FlexColDiv } from '../../style/style';
import PlayBtn from '../button/playBtn';

import {
  Container,
  PlayerContainer,
  FigureIcon,
  Icon,
  SVGIcon,
  Timer,
} from './themeStyles';

const svgConfig = {
  width: 453,
  height: 453,
  viewBox: '0 0 453 453',
  fill: 'none',
  xmlns: 'http://www.w3.org/2000/svg',
};

const circleConfig = {
  cx: 226.5,
  cy: 226.5,
  r: 216.5,
};

function Theme(props) {
  return (
    <Container>
      <Title size="md">No one save us but ourselves</Title>

      <PlayerContainer>
        {/* <audio class="music">
          <source src={OceanSound} />
        </audio> */}

        <FigureIcon src={MeditatorIcon} alt="meditator" />

        <SVGIcon
          // class="track-outline"
          {...svgConfig}>
          <circle {...circleConfig} stroke="white" stroke-width="20" />
        </SVGIcon>
        <SVGIcon
          // class="moving-outline"
          {...svgConfig}>
          <circle {...circleConfig} stroke="#018EBA" stroke-width="20" />
        </SVGIcon>
      </PlayerContainer>

      <FlexColDiv>
        <Timer size="sm">0:00</Timer>
        <PlayBtn />
      </FlexColDiv>
      {props.children}
    </Container>
  );
}

export default Theme;
