import React from 'react';

import { Icon } from '../components/theme/themeStyles';
import Theme from '../components/theme/theme';

import OceanSound from '../sounds/aquatic.mp3';
import Wave from '../images/wave-2.png';
import Moon from '../images/moon.png';

function OceanWave(props) {
  let audio = new Audio(OceanSound);
  //const playAudio = audio => audio.play();

  return (
    <>
      <Theme onClick={playAudio} audio={audio}>
        <Icon src={Wave} wave />
        <Icon src={Moon} moon />

        {/* <audio className="music">
        <source src={OceanSound} />
      </audio> */}
      </Theme>

      <button onClick={audio.play}>play music</button>
    </>
  );
}

export default OceanWave;
