import React from 'react';
import styled from 'styled-components';

import { Title } from './landing';

const SubTitle = styled.h2`
  font-size: 2rem;
  font-weight: 400;
`;

const Container = styled.div`
  height: 100%;
  text-align: center;
`;

const Card = styled.div`
  width: 300px;
  height: 350px;
  border-radius: 7px;
  background: #fff;
  box-shadow: 0 19px 19px 0 rgba($shadow-color, 0.3),
    0 15px 6px 0 rgba(#000000, #000000, 0.22);
`;

function Theme() {
  return (
    <Container>
      <Title thin>
        Welcome, <strong>Suyeon</strong>
      </Title>
      <SubTitle>Choose your favorite theme</SubTitle>

      <Card />
    </Container>
  );
}

export default Theme;
