import React, { useState, useEffect } from 'react';

import {
  Container,
  PlayerContainer,
  FigureIcon,
  SVGIcon,
  Timer,
} from './themeStyles';
import MeditatorIcon from '../../images/figure-2.svg';
import { Title, FlexColDiv } from '../../style/style';
import Player from '../../hooks/useAudio';

const svgConfig = {
  width: 453,
  height: 453,
  viewBox: '0 0 453 453',
  fill: 'none',
  xmlns: 'http://www.w3.org/2000/svg',
};

const circleConfig = {
  cx: 226.5,
  cy: 226.5,
  r: 216.5,
};

function Theme(props) {
  const { url } = props;

  const timeCalcurator = time => {
    // 300
    const min = Math.floor(time / 60);
    const sec = Math.floor(time % 60);
    const timeLeft = `${min}: ${sec}`;
    return timeLeft;
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      setTimeLeft(timeCalcurator());
    }, 1000);
    return () => clearTimeout(timer);
  });

  const [timeLeft, setTimeLeft] = useState(timeCalcurator());

  return (
    <Container>
      <Title size="md">No one save us but ourselves</Title>

      <PlayerContainer>
        <FigureIcon src={MeditatorIcon} alt="meditator" />

        <SVGIcon
          // class="track-outline"
          {...svgConfig}>
          <circle {...circleConfig} stroke="white" strokeWidth="20" />
        </SVGIcon>
        <SVGIcon
          // class="moving-outline"
          {...svgConfig}>
          <circle {...circleConfig} stroke="#018EBA" strokeWidth="20" />
        </SVGIcon>
      </PlayerContainer>

      <FlexColDiv>
        {/* <Timer size="sm">0:00</Timer> */}
        <Timer size="sm">{timeLeft}</Timer>
        <Player url={url} />
        <button onClick={() => timeCalcurator(300)}>5 min</button>
        <button onClick={() => timeCalcurator(600)}>10 min</button>
        <button onClick={() => timeCalcurator(900)}>15 min</button>
      </FlexColDiv>
      {props.children}
    </Container>
  );
}

export default Theme;
