import React, { useRef, useEffect } from 'react';

import MeditatorIcon from '../../images/figure-2.svg';
import { PlayerContainer, FigureIcon, SVGIcon } from '../theme/themeStyles';

function TimeLoader() {
  const circleRef = useRef();
  useEffect(() => {
    const outlineLength = circleRef.getTotalLength();
    console.log(outlineLength);
  }, []);

  const svgConfig = {
    width: 453,
    height: 453,
    viewBox: '0 0 453 453',
    fill: 'none',
    xmlns: 'http://www.w3.org/2000/svg',
  };

  const circleConfig = {
    cx: 226.5,
    cy: 226.5,
    r: 216.5,
  };

  return (
    <PlayerContainer>
      <FigureIcon src={MeditatorIcon} alt="meditator" />

      <SVGIcon
        // class="track-outline"
        {...svgConfig}>
        <circle {...circleConfig} stroke="white" strokeWidth="20" />
      </SVGIcon>
      <SVGIcon
        // class="moving-outline"
        {...svgConfig}>
        <circle
          {...circleConfig}
          stroke="#018EBA"
          strokeWidth="20"
          ref={circleRef}
        />
      </SVGIcon>
    </PlayerContainer>
  );
}

export default TimeLoader;
