import React, { useState, useEffect } from 'react';

import { Container } from './themeStyles';
import { Title, FlexColDiv } from '../../style/style';
import ProgressBar from '../progressBar/progressBar';
import TimerBtn from '../button/TimerBtn';
import PlayBtn from '../button/playBtn';

// Container

const Player = ({ setStart, playing, toggle }) => {
  const handleClick = () => {
    toggle();
    setStart();
  };

  return <PlayBtn onClick={handleClick} playing={playing} />;
};

function Theme(props) {
  const { url } = props;

  // Audio
  const [audio] = useState(new Audio(url));
  const [playing, setPlaying] = useState(false);

  const [start, setStart] = useState(false);
  const [duration, setDuration] = useState({ min: 60, sec: 0 });
  const { min, sec } = duration;

  const currentTime = audio.currentTime;
  let elapsed = min - currentTime;

  useEffect(() => {
    playing ? audio.play() : audio.pause();
  }, [playing, audio]);

  useEffect(() => {
    audio.addEventListener('ended', () => setPlaying(false));
    return () => {
      audio.removeEventListener('ended', () => setPlaying(false));
    };
  }, [audio]);

  const toggle = () => setPlaying(!playing);

  const handlePlay = () => {
    toggle();
    setStart();
  };

  audio.ontimeupdate = () => {
    if (currentTime >= min) {
      audio.pause();
      audio.currentTime = 0;
      setPlaying(false);
    }
  };

  /////

  const displayTime = `${Math.floor(min / 60)}:${Math.floor(sec % 60)}`;

  const handleClick = time => {
    setDuration({ min: time, sec: 0 });
  };

  const handleToggle = () => {
    setStart(!start);
  };

  const updateTime = () => {
    if (start) {
      if (sec === 0) {
        setDuration({
          min: min - 1,
          sec: 59,
        });
      } else {
        setDuration({ ...duration, sec: sec - 1 });
      }
    }
  };

  useEffect(() => {
    if (start && currentTime < min) {
      const token = setTimeout(updateTime, 1000);

      return function cleanUp() {
        clearTimeout(token);
      };
    }
  });

  return (
    <Container>
      <Title size="md">No one save us but ourselves</Title>
      <ProgressBar
        duration={min}
        displayTime={displayTime}
        currentTime={currentTime}
        start={start}
      />
      <FlexColDiv>
        <Player
          url={url}
          setStart={handleToggle}
          toggle={toggle}
          playing={playing}
        />
        <TimerBtn onClick={handleClick} />
      </FlexColDiv>
      {props.children}
    </Container>
  );
}

