import React from 'react';
import styled from 'styled-components';

import { Title } from '../style/style';
import WaveIcon from '../images/wave-1.png';
import mountainIcon from '../images/mountain.png';
import rainIcon from '../images/rain.png';

const Container = styled.div`
  height: 100%;
  text-align: center;
`;

const themes = [
  { img: WaveIcon, title: 'Ocean waves' },
  { img: mountainIcon, title: 'Mountain view' },
  { img: rainIcon, title: 'Standing in the rain' },
];

function Theme() {
  return (
    <Container>
      <Title size="md">
        Welcome, <strong>Suyeon</strong>
      </Title>
      <Title size="sm">Choose your favorite theme</Title>
    </Container>
  );
}

export default Theme;
