import React, { useRef, useEffect, useState } from 'react';

import MeditatorIcon from '../../assets/figure-2.svg';
import { PlayerContainer, FigureIcon, SVGIcon } from '../theme/themeStyles';

function ProgressBar({ duration }) {
  const circleRef = useRef();
  const [length, setLength] = useState();

  useEffect(() => {
    const circleLength = circleRef.current.getTotalLength();
    setLength(circleLength);
    console.log(circleLength);
  }, []);

  const svgConfig = {
    width: 453,
    height: 453,
    viewBox: '0 0 453 453',
    fill: 'none',
    xmlns: 'http://www.w3.org/2000/svg',
  };

  const circleConfig = {
    cx: 226.5,
    cy: 226.5,
    r: 216.5,
  };

  // Progress bar based on sound
  // DOMstring.sound.ontimeupdate = () => {
  //   let currentTime = DOMstring.sound.currentTime;
  //   let elapsed = duration - currentTime;
  //   let seconds = Math.floor(elapsed % 60);
  //   let minutes = Math.floor(elapsed / 60);
  //   let progress = length - (currentTime / duration) * length;

  //   DOMstring.outline.style.strokeDashoffset = progress;

  //   // Reset when time is over
  //   if (currentTime >= duration) {
  //     DOMstring.sound.pause();
  //     DOMstring.sound.currentTime = 0;
  //     DOMstring.playBtn.src = './img/svg/play.svg';
  //   }
  // };

  return (
    <PlayerContainer>
      <FigureIcon src={MeditatorIcon} alt="meditator" />

      <SVGIcon
        // class="track-outline"
        {...svgConfig}>
        <circle {...circleConfig} stroke="white" strokeWidth="20" />
      </SVGIcon>
      <SVGIcon
        // class="moving-outline"
        {...svgConfig}>
        <circle
          {...circleConfig}
          stroke="#018EBA"
          strokeWidth="20"
          ref={circleRef}
          //style={{ strokeDasharray: length, strokeDashoffset: length }}
        />
      </SVGIcon>
    </PlayerContainer>
  );
}

export default ProgressBar;
