import React, { useState, useEffect } from 'react';
import { Redirect, useLocation } from 'react-router-dom';

import { Container } from './ThemeStyles';
import ProgressBar from '../../components/ProgressBar/ProgressBar';
import TimerBtn from '../../components/Button/TimerBtn';
import PlayBtn from '../../components/Button/PlayBtn';

import DynamicText from '../../components/DynamicText/DynamicText';

function Theme(props) {
  const { url } = props;
  //const [audio] = useState(new Audio(url));
  const [playing, setPlaying] = useState(false);
  const [duration, setDuration] = useState(10);
  const [currentTime, setCurrentTime] = useState(0);

  const audio = new Audio(url);
  // const location = useLocation();
  // useEffect(() => {
  //   const playback = new Sound(audioSource, '', error => {
  //     if (error) {
  //       console.tron.log('failed to load the sound', error);
  //     }

  //     setDuration(playback.getDuration());
  //     console.tron.log('loaded successfully');
  //   });

  //   setFile(playback);
  //   return () => {
  //     // --> componentWillUnmount
  //     playback.stop();
  //     playback.release();
  //   };
  // }, []);

  // Play audio
  useEffect(() => {
    // setPlaying(false)
    playing ? audio.play() : audio.pause();
  }, [playing, audio]);

  useEffect(() => {
    audio.addEventListener('ended', () => setPlaying(false));
    return () => {
      audio.removeEventListener('ended', () => setPlaying(false));
    };
  }, [audio]);

  //Update duration
  useEffect(() => {
    if (currentTime >= duration) return;
    if (playing) {
      const token = setTimeout(updateTime, 1000);

      return function cleanUp() {
        clearTimeout(token);
      };
    }
  });

  const handleClick = time => setDuration(time);
  const handleToggle = () => setPlaying(!playing);
  const updateTime = () => {
    if (playing) setDuration(duration - 1);
  };

  // HERE Warging!
  audio.ontimeupdate = () => {
    setCurrentTime(audio.currentTime);
    if (currentTime > duration) {
      audio.pause();
      audio.currentTime = 0;
      setPlaying(false);
    }
  };

  if (currentTime >= duration) {
    return <Redirect to="/congraturation" />;
  }

  return (
    <Container>
      <DynamicText playing={playing} />
      <ProgressBar
        playing={playing}
        duration={duration}
        currentTime={Math.floor(currentTime)}
      />
      <PlayBtn playing={playing} onClick={handleToggle} />
      <TimerBtn onClick={handleClick} />
      {props.children}
    </Container>
  );
}

export default React.memo(Theme);
