import React, { useState, useEffect } from 'react';
import { Title } from '../../style/style';

function DynamicText({ playing, textArr }) {
  const [textIdx, setTextIdx] = useState(0);

  //const textArr = ['Breath in', 'Breath out', 'Just focus on yourself'];
  let textThatChanges = textArr[textIdx % textArr.length];

  useEffect(() => {
    if (playing) {
      const interval = setInterval(() => {
        setTextIdx(index => index + 1);
      }, 3000);
      return () => clearInterval(interval);
    }
  }, [playing]);

  return <Title size="sm">{textThatChanges}</Title>;
}

export default DynamicText;
