import React, { useState } from 'react';
import styled from 'styled-components';

const BtnContainer = styled.div`
  position: absolute;
  top: 5rem;
  right: 0;
  border-radius: 7px 0 0 7px;
  color: white;
  background: black;

  width: ${props => (props.isOpen ? '10rem' : '3rem')};
  height: 8rem;
  display: flex;
  justify-content: ${props => (props.isOpen ? 'flex-start' : 'center')};
  align-items: center;
  cursor: pointer;
`;

const Span = styled.span`
  display: inline-block;
  transform: rotate(270deg);
  letter-spacing: 4px;
  font-size: 1.2rem;
`;

const BtnsContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const Btn = styled.button`
  background: transparent;
  margin: 5px;
  color: white;
  font-size: 1rem;
  font-weight: 700;
  text-align: left;
`;

function TimerBtn({ onClick }) {
  const [isOpen, setIsOpen] = useState(false);

  const times = ['5 min', '10 min', '15min'];
  const buttons = times.map((t, i) => <Btn>{t}</Btn>);

  const handleClick = e => {
    if (e.target !== e.currentTarget) return;
    setIsOpen(!isOpen);
  };

  return (
    <BtnContainer onClick={handleClick} isOpen={isOpen}>
      <Span>TIMER</Span>
      <BtnsContainer>{isOpen && buttons}</BtnsContainer>
    </BtnContainer>
  );
}

export default TimerBtn;
