import React, { useRef, useEffect, useState } from 'react';
//import styled from 'styled-components';

import MeditatorIcon from '../../assets/figure-2.svg';
import {
  PlayerContainer,
  FigureIcon,
  SVGIcon,
  Timer,
} from '../theme/themeStyles';

function ProgressBar({ duration, displayTime, currentTime, start }) {
  const circleRef = useRef();
  const [length, setLength] = useState();

  const progress = length - (currentTime / duration) * length;

  useEffect(() => {
    const circleLength = circleRef.current.getTotalLength();
    setLength(circleLength);
    console.log(circleLength);
  }, []);

  const svgConfig = {
    width: 453,
    height: 453,
    viewBox: '0 0 453 453',
    fill: 'none',
    xmlns: 'http://www.w3.org/2000/svg',
  };

  const circleConfig = {
    cx: 226.5,
    cy: 226.5,
    r: 216.5,
  };

  return (
    <PlayerContainer>
      <FigureIcon src={MeditatorIcon} alt="meditator" />

      <SVGIcon {...svgConfig}>
        <circle {...circleConfig} stroke="#A0BDE6" strokeWidth="20" />
      </SVGIcon>
      <SVGIcon {...svgConfig}>
        <circle
          {...circleConfig}
          stroke="#3680EA"
          strokeWidth="20"
          ref={circleRef}
          style={{
            strokeDasharray: length,
            strokeDashoffset: start ? progress : length,
          }}
        />
      </SVGIcon>

      <Timer size="sm">{displayTime}</Timer>
    </PlayerContainer>
  );
}

export default ProgressBar;

