import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';

// const fadein = keyframes`
//   from   { opacity:0; }
//   to { opacity:1; }
// `;

// const fadeout = keyframes`
//   from   { opacity:1; }
//   to { opacity:0; }
// `;

// const stateToAnimation = {
//   NO_SLIDE: 'none',
//   SLIDE_OUT: fadein + ' forwards',
//   SLIDE_IN: fadeout,
// }

const Text = styled.h3`
  text-align: center;
  font-size: 1.7rem;
  font-weight: 400;
  letter-spacing: 1px;

  ${'' /* opacity: 0;
  animation: ${props => props.playing && `${fade} 3s infinite`}; */}

  .fadeOut {
    opacity: 0;
    width: 0;
    height: 0;
    transition: width 0.5s 0.5s, height 0.5s 0.5s, opacity 0.5s;
  }
  .fadeIn {
    opacity: 1;
    width: 100px;
    height: 100px;
    transition: width 0.5s, height 0.5s, opacity 0.5s 0.5s;
  }
`;

function DynamicText({ playing, textArr }) {
  const [textIdx, setTextIdx] = useState(0);
  let textThatChanges = textArr[textIdx % textArr.length];

  useEffect(() => {
    if (playing) {
      const interval = setInterval(() => {
        setTextIdx(index => index + 1);
      }, 3000);
      return () => clearInterval(interval);
    }
  }, [playing]);

  return <Text>{textThatChanges}</Text>;
}

export default DynamicText;
