import styled, {
  keyframes
} from 'styled-components';
import {
  Title
} from '../../style/style';

export const Container = styled.div `
  position: relative;
  width: 100vw;
  height: 92vh;
  overflow: hidden;
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

export const PlayerContainer = styled.div `
  position: relative;
  width: 19rem;
  height: 19rem;
  margin: 0 auto;
`;

export const SVGIcon = styled.svg `
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  height: 15rem;
  pointer-events: none;
`;

const pulse = styled.keyframes `
  0% { transform: scale(0.95) }
  70% { transform: scale(1) }
  100% { transform: scale(0.95) }
`;

export const FigureIcon = styled.img `
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 65%;
`;

export const Timer = styled(Title)
`
  position: absolute;
  bottom: 17%;
  left: 50%;
  transform: translateX(-50%);
  margin: 0;
  text-align: center;
`;

export const Icon = styled.img `
  position: absolute;
  height: auto;
  z-index: -1;
`;

export const WaveIcon = styled(Icon)
`
  width: 50%;
  left: 50%;
  bottom: 3rem;
  transform: translateX(-50%);
`;

export const MoonIcon = styled(Icon)
`
  width: 15%;
  top: 2rem;
  right: 10rem;
`;

export const BigMountainIcon = styled(Icon)
`
  width: 35%;
  left: 13rem;
  bottom: -13rem;
`;

export const SmallMountainIcon = styled(Icon)
`
  width: 30%;
  right: 20rem;
  bottom: -19rem;
`;

export const LeftRainIcon = styled(Icon)
`
  width: 17%;
  left: 13rem;
  top: 13rem;
`;

export const RightRainIcon = styled(Icon)
`
  width: 17%;
  right: 17rem;
  top: 20rem;
`;
