import React, { useState, useEffect } from 'react';
import { Transition } from 'react-transition-group';

import { SmallTitle } from '../../style/style';

function DynamicText({ playing }) {
  const [textIdx, setTextIdx] = useState(0);

  const textArr = ['Breath in', 'Breath out', 'Just focus on yourself'];
  let textThatChanges = textArr[textIdx % textArr.length];

  const [text, setText] = useState(textArr[textIdx]);
  console.log(text);

  // const duration = 300;

  // const defaultStyle = {
  //   transition: `opacity ${duration}ms ease-in-out`,
  //   opacity: 0,
  // }

  // const transitionStyles = {
  //   entering: { opacity: 1 },
  //   entered:  { opacity: 1 },
  //   exiting:  { opacity: 0 },
  //   exited:  { opacity: 0 },
  // };

  useEffect(() => {
    if (playing) {
      const interval = setInterval(() => {
        setTextIdx(index => index + 1);
      }, 3000);
      return () => {
        clearInterval(interval);
      };
    }
  }, [playing]);

  return <SmallTitle>{textThatChanges}</SmallTitle>;
}

export default DynamicText;

