import React from 'react';
import styled from 'styled-components';

import { Title } from '../style/style';
import Sound from '../sounds/aquatic.mp3';
import MeditatorIcon from '../images/meditator.svg';

const PlayerContainer = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  flex: 1;
`;
// setTimeOut: chaing title

const SVGIcon = styled.svg`
  position: absolute;
  height: 13rem;
  top: 50%;
  left: 55%;
  transform: translate(-50%, -50%) rotate(-90deg);
  pointer-events: none;
`;

const Icon = styled.img`
  position: absolute;
`;

function OceanWave() {
  return (
    <div>
      <Title size="md">No one save us but ourselves</Title>

      <PlayerContainer>
        <audio class="music">
          <source src={Sound} />
        </audio>

        <img src={MeditatorIcon} alt="meditator" />

        <SVGIcon
          class="track-outline"
          width="453"
          height="453"
          viewBox="0 0 453 453"
          fill="none"
          xmlns="http://www.w3.org/2000/svg">
          <circle
            cx="226.5"
            cy="226.5"
            r="216.5"
            stroke="white"
            stroke-width="20"
          />
        </SVGIcon>
        <SVGIcon
          class="moving-outline"
          width="453"
          height="453"
          viewBox="0 0 453 453"
          fill="none"
          xmlns="http://www.w3.org/2000/svg">
          <circle
            cx="226.5"
            cy="226.5"
            r="216.5"
            stroke="#018EBA"
            stroke-width="20"
          />
        </SVGIcon>
        <h3 class="time-display">0:00</h3>
      </PlayerContainer>
    </div>
  );
}

export default OceanWave;
