import styled, { css } from 'styled-components';

const sizes = {
  lg: {
    fontWeight: 700,
    fontSize: '3rem',
  },
  md: {
    fontWeight: 400,
    fontSize: '3rem',
  },
  sm: {
    fontWeight: 400,
    fontSize: '1.6rem',
  },
  xs: {
    fontWeight: 400,
    fontSize: '1.2rem',
  },
};

const sizeStyles = css`
  ${({ size }) => css`
    font-weight: ${sizes[size].fontWeight};
    font-size: ${sizes[size].fontSize};
  `}
`;

export const Title = styled.h1`
  margin-bottom: 2rem;
  ${sizeStyles}
`;

export const Wrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;
