import React, { useState, useEffect } from 'react';
import PlayBtn from '../components/button/playBtn';

const useAudio = url => {
  const [audio] = useState(new Audio(url));
  const [playing, setPlaying] = useState(false);

  // Stop music when time is over
  console.log(600 - audio.currentTime);

  // audio.ontimeupdate = () => {
  //   let currentTime = audio.currentTime;
  //   let elapsed = duration - currentTime;

  //   let seconds = Math.floor(elapsed % 60);
  //   let minutes = Math.floor(elapsed / 60);

  //   let progress = length - (currentTime / duration) * outlineLength;
  //   DOMstring.outline.style.strokeDashoffset = progress;
  // }

  const toggle = () => setPlaying(!playing);

  useEffect(() => {
    playing ? audio.play() : audio.pause();
  }, [playing, audio]);

  useEffect(() => {
    audio.addEventListener('ended', () => setPlaying(false));
    return () => {
      audio.removeEventListener('ended', () => setPlaying(false));
    };
  }, [audio]);

  return [playing, toggle];
};

const Player = ({ url, setStart }) => {
  const [playing, toggle] = useAudio(url);

  const handleClick = () => {
    toggle();
    setStart();
  };

  return <PlayBtn onClick={handleClick} playing={playing} />;
};

export default Player;

