import React from 'react';

import { Icon } from '../components/theme/themeStyles';
import Theme from '../components/theme/theme';

import Wave from '../images/wave-2.png';
import Moon from '../images/moon.png';

import Player from '../hooks/useAudio';

function OceanWave(props) {
  let audio = new Audio('/assets/aquatic.mp3');
  //const playAudio = () => audio.play();

  const playAudio = () => {
    if (audio.play) {
      audio.pause();
    } else {
      audio.play();
    }
  };

  return (
    <Theme url="/assets/aquatic.mp3">
      <Icon src={Wave} wave />
      <Icon src={Moon} moon />
    </Theme>
  );
}

export default OceanWave;
