import React from 'react';
import styled from 'styled-components';

import Figure from '../assets/figure-3.svg';
import { Title } from '../style/style';

const Container = styled.div`
  overflow: hidden;
  width: 100vw;
  height: 92vh;
  display: flex;
  align-items: center;
  justify-content: center;

  @media (max-width: 900px) {
    flex-direction: column;
  }
`;

const FigureIcon = styled.img`
  width: 30rem;
  height: auto;
  transform: translateY(7rem);

  @media (max-width: 1200px) {
    width: 40rem;
    ${'' /* width: 600px; */}
  }

  @media (max-width: 900px) {
    width: 20rem;
    transform: translateY(0);
  }

  @media (max-width: 576px) {
    width: 16rem;
  }

  @media (max-width: 320px) {
    width: 14rem;
  }
`;

const Span = styled.span`
  display: block;
  margin-top: 5rem;
  width: 80%;

  @media (max-width: 900px) {
    ${'' /* width: 100%; */}
    text-align: center;
    width: 100%;
    margin-top: 2rem;
    font-size: 2.3rem;
  }

  @media (max-width: 576px) {
    font-size: 2rem;
    padding: 0 10px;
  }
`;

function Congraturation() {
  return (
    <Container>
      <FigureIcon src={Figure} />
      <Title size="xl">
        Congraturation!
        <Span>I wish you have a good day.</Span>
      </Title>
    </Container>
  );
}

export default Congraturation;
