import React from 'react';

import {
  Container,
  PlayerContainer,
  FigureIcon,
  SVGIcon,
  Timer,
} from './themeStyles';
import MeditatorIcon from '../../images/figure-2.svg';
import { Title, FlexColDiv } from '../../style/style';
import PlayBtn from '../button/playBtn';

const svgConfig = {
  width: 453,
  height: 453,
  viewBox: '0 0 453 453',
  fill: 'none',
  xmlns: 'http://www.w3.org/2000/svg',
};

const circleConfig = {
  cx: 226.5,
  cy: 226.5,
  r: 216.5,
};

function Theme(props) {
  let audio = new Audio('../../sounds/aquatic.mp3');

  return (
    <Container>
      <Title size="md">No one save us but ourselves</Title>

      <PlayerContainer>
        {/* <audio class="music">
          <source src={OceanSound} />
        </audio> */}

        <FigureIcon src={MeditatorIcon} alt="meditator" />

        <SVGIcon
          // class="track-outline"
          {...svgConfig}>
          <circle {...circleConfig} stroke="white" strokeWidth="20" />
        </SVGIcon>
        <SVGIcon
          // class="moving-outline"
          {...svgConfig}>
          <circle {...circleConfig} stroke="#018EBA" strokeWidth="20" />
        </SVGIcon>
      </PlayerContainer>

      <FlexColDiv>
        <Timer size="sm">0:00</Timer>

        <PlayBtn onClick={audio.play} />
        <audio className="music">
          <source src="../../sounds/aquatic.mp3" />
        </audio>
      </FlexColDiv>
      {props.children}
    </Container>
  );
}

export default Theme;
