import React from 'react';
import { Route, Switch } from 'react-router-dom';
import './App.css';
import { TransitionGroup, CSSTransition } from 'react-transition-group';

import Layout from './components/Layout/Layout';
import Themes from './pages/Themes';
import Register from './pages/Register';
import OceanWave from './pages/OceanWave';
import Mountain from './pages/Mountain';
import Rain from './pages/Rain';
import Congraturation from './pages/Congraturation';

// Congraturation page
// Animation & page transition
// Stop sound when go back to theme page
// Media query, optimization

// Clutter folders
// Duration: maximum 8 min.

function App({ location }) {
  // Redirect - Guard
  // Repeated localStorage
  // Height & width

  return (
    <div className="App">
      <Layout>
        <TransitionGroup component={null}>
          <CSSTransition key={location.key} in={true} classNames="slide-in">
            <Switch location={location}>
              <Route path="/bgsearch/" exact render={() => <BgSearch />} />
              <Route path="/usersearch/" exact render={() => <UserSearch />} />
            </Switch>
          </CSSTransition>
        </TransitionGroup>
        <Route
          render={({ location }) => (
            <TransitionGroup>
              <CSSTransition key={location.key} timeout={300} classNames="fade">
                <Switch>
                  <Route path="/" exact={true} component={Register} />
                  <Route path="/themes" component={Themes} />
                  <Route path="/ocean" component={OceanWave} />
                  <Route path="/mountain" component={Mountain} />
                  <Route path="/rain" component={Rain} />
                  <Route path="/congraturation" component={Congraturation} />
                </Switch>
              </CSSTransition>
            </TransitionGroup>
          )}
        />
      </Layout>
    </div>
  );
}

export default App;

