import React, { useState, useEffect } from 'react';

import { Container, Timer } from './themeStyles';
import { Title, FlexColDiv } from '../../style/style';
import Player from '../../hooks/useAudio';
import ProgressBar from '../progressBar/progressBar';

import CircleProgressBarBase from '../circleProgressBar/CircleProgressBarBase';

// 600 (5min)
// 600 - current
function Theme(props) {
  const { url } = props;

  const [start, setStart] = useState(false);
  const [timeTotal, setTimeTodal] = useState(60);
  const [timeLeft, setTimeLeft] = useState();

  //const displayTime = `${Math.floor(min / 60)}:${Math.floor(sec % 60)}`;

  const handleClick = time => setTimeTodal(time);
  const handleToggle = () => setStart(!start);

  const updateTime = () => {
    if (start && timeLeft) {
      const current = timeLeft - 6;
      setTimeLeft(current);
    } else if (timeLeft === 0) {
      setStart(false);
    } else {
      const current = timeTotal - 6;
      setTimeLeft(current);
    }
  };

  useEffect(() => {
    if (start) {
      const token = setTimeout(updateTime, 1000);

      return function cleanUp() {
        clearTimeout(token);
      };
    }
  });

  const displayTime = Math.floor((timeLeft / timeTotal) * 100);

  return (
    <Container>
      <Title size="md">No one save us but ourselves</Title>
      {/* <ProgressBar duration={duration} /> */}
      <CircleProgressBarBase
        trailStrokeColor="gray"
        strokeColor="#8B37F5"
        percentage={displayTime}
        innerText="complete"
        start={start}
      />

      <FlexColDiv>
        <Timer size="sm">{displayTime}</Timer>
        <Player url={url} setStart={handleToggle} />

        <button onClick={() => handleClick(300)}>5 min</button>
        <button onClick={() => handleClick(600)}>10 min</button>
        <button onClick={() => handleClick(900)}>15 min</button>
      </FlexColDiv>
      {props.children}
    </Container>
  );
}

export default Theme;

