import React from 'react';
import styled from 'styled-components';

import { Title, FlexColDiv } from '../style/style';
import PlayBtn from '../components/button/playBtn';

import Sound from '../sounds/aquatic.mp3';
import MeditatorIcon from '../images/meditator.svg';

import Wave from '../images/wave-2.png';
import Moon from '../images/moon.png';
import Mountain from '../images/mountain-2.png';
import Rain from '../images/rain.png';

// setTimeOut: chaing title

export const Container = styled.div`
  position: relative;
  width: 100vw;
  height: 92vh;
  overflow: hidden;
  text-align: center;

  display: flex;
  flex-direction: column;
  justify-content: center;
`;

export const PlayerContainer = styled.div`
  position: relative;
  width: 15rem;
  height: 15rem;
  margin: 0 auto;
  ${'' /* display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  flex: 1; */}
`;

export const SVGIcon = styled.svg`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  height: 13rem;
  ${'' /* top: 50%;
  left: 55%;
  transform: translate(-50%, -50%) rotate(-90deg);
  pointer-events: none; */}
`;

export const Icon = styled.img`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 80%;
`;

export const Timer = styled(Title)`
  margin: 0;
  text-align: center;
`;

export const WaveIcon = styled.img`
  position: absolute;
  width: 50%;
  height: auto;
  z-index: -1;
  left: 50%;
  bottom: 3rem;
  transform: translateX(-50%);
`;

export const MoonIcon = styled.img`
  position: absolute;
  width: 15%;
  height: auto;
  z-index: -1;
  top: 2rem;
  right: 10rem;
`;

export const MountainIconBig = styled.img`
  position: absolute;
  width: 35%;
  height: auto;
  z-index: -1;
  left: 13rem;
  bottom: -13rem;
`;

export const MountainIconSmall = styled.img`
  position: absolute;
  width: 30%;
  height: auto;
  z-index: -1;
  right: 20rem;
  bottom: -19rem;
`;

const RainIconLeft = styled.img`
  position: absolute;
  width: 15%;
  height: auto;
  z-index: -1;
  left: 13rem;
  top: 5rem;
`;

const svgConfig = {
  width: 453,
  height: 453,
  viewBox: '0 0 453 453',
  fill: 'none',
  xmlns: 'http://www.w3.org/2000/svg',
};

const circleConfig = {
  cx: 226.5,
  cy: 226.5,
  r: 216.5,
};

function OceanWave() {
  return (
    <Container>
      <Title size="md">No one save us but ourselves</Title>

      <PlayerContainer>
        <audio class="music">
          <source src={Sound} />
        </audio>

        <Icon src={MeditatorIcon} alt="meditator" />

        <SVGIcon
          // class="track-outline"
          {...svgConfig}>
          <circle {...circleConfig} stroke="white" stroke-width="20" />
        </SVGIcon>
        <SVGIcon
          // class="moving-outline"
          {...svgConfig}>
          <circle {...circleConfig} stroke="#018EBA" stroke-width="20" />
        </SVGIcon>
      </PlayerContainer>

      <FlexColDiv>
        <Timer size="sm">0:00</Timer>
        <PlayBtn />
      </FlexColDiv>
      {/* 
      <WaveIcon src={Wave} />
      <MoonIcon src={Moon} /> */}

      {/* <MountainIconBig src={Mountain} />
      <MountainIconSmall src={Mountain} /> */}

      <RainIconLeft src={Rain} />
    </Container>
  );
}

export default OceanWave;
