import React from 'react';
import { Route, Switch } from 'react-router-dom';
import './App.css';

import Layout from './components/layout/layout';
import Themes from './pages/themes';
import Register from './pages/register';

import OceanWave from './pages/oceanWave';
import Mountain from './pages/mountain';
import Rain from './pages/rain';
import Congraturation from './pages/congraturation';

// Height & width
// !localSrorage && landingPage
// Animation & page transition
// Set timer modal

function App() {
  const name = localStorage.getItem('name');
  console.log(name);

  return (
    <div className="App">
      <Layout>
        {/* {!name && <Register />} */}
        <Switch>
          <Route path="/" exact={true} component={Themes} />
          {/* <Route path="/" exact={true} component={name ? Themes : Register} /> */}
          <Route
            exact={true}
            path="/"
            render={props => {
              if (name) return <Themes />;
              else return <Register />;
            }}
          />
          <Route path="/ocean" component={OceanWave} />
          <Route path="/mountain" component={Mountain} />
          <Route path="/rain" component={Rain} />
          <Route path="/b" component={Congraturation} />
        </Switch>
      </Layout>
    </div>
  );
}

export default App;
