import React from 'react';
import styled, { css } from 'styled-components';

import Button from '../components/button/button';
import Icon from '../images/heart.svg';

const Container = styled.div`
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 85vh;
`;

const sizes = {
  large: {
    fontWeight: 700,
    fontSize: '3rem',
  },
  medium: {
    fontWeight: 400,
    fontSize: '3rem',
  },
  small: {
    height: 400,
    fontSize: '2rem',
  },
};

const sizeStyles = css`
  ${({ size }) => css`
    font-weight: ${sizes[size].fontWeight};
    font-size: ${sizes[size].fontSize};
  `}
`;

export const Title = styled.h1`
  margin-bottom: 2rem;
  ${'' /* font-size: 3rem;
  font-weight: ${props => (props.thin ? '400' : '700')}; */}
  ${sizeStyles}
`;

const Input = styled.input`
  width: 100%;
  height: 50px;
  font-size: 2.5rem;
  text-align: center;
  border: none;
  outline: none;
  border-bottom: 3px solid black;
  margin-bottom: 5rem;
`;

const QuestionContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const HeartIcon = styled.div`
  position: absolute;
  left: 5rem;
  bottom: 0;
  width: 400px;
  height: 400px;
  transform: rotate(-13deg);
  background: url(${Icon}) center center no-repeat;
  background-size: 90%;
`;

function Landing() {
  return (
    <Container>
      <QuestionContainer>
        <Title size="large">What is your name?</Title>
        <Input />
        <Button />
      </QuestionContainer>
      <HeartIcon />
    </Container>
  );
}

export default Landing;
