import React from 'react';
import styled from 'styled-components';

import { Title } from '../style/style';
import Sound from '../sounds/aquatic.mp3';
import MeditatorIcon from '../images/meditator.svg';

// setTimeOut: chaing title

const PlayerContainer = styled.div`
  position: relative;
  background: yellow;
  width: 15rem;
  height: 15rem;
  margin: 0 auto;
  ${'' /* display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  flex: 1; */}
`;

const SVGIcon = styled.svg`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  height: 13rem;
  ${'' /* top: 50%;
  left: 55%;
  transform: translate(-50%, -50%) rotate(-90deg);
  pointer-events: none; */}
`;

const Icon = styled.img`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 70%;
`;

const Timer = styled(Title)`
  text-align: center;
  margin-top: 3rem;
`;

function OceanWave() {
  return (
    <div>
      <Title size="md">No one save us but ourselves</Title>

      <PlayerContainer>
        <audio class="music">
          <source src={Sound} />
        </audio>

        <Icon src={MeditatorIcon} alt="meditator" />

        <SVGIcon
          class="track-outline"
          width="453"
          height="453"
          viewBox="0 0 453 453"
          fill="none"
          xmlns="http://www.w3.org/2000/svg">
          <circle
            cx="226.5"
            cy="226.5"
            r="216.5"
            stroke="white"
            stroke-width="20"
          />
        </SVGIcon>
        <SVGIcon
          class="moving-outline"
          width="453"
          height="453"
          viewBox="0 0 453 453"
          fill="none"
          xmlns="http://www.w3.org/2000/svg">
          <circle
            cx="226.5"
            cy="226.5"
            r="216.5"
            stroke="#018EBA"
            stroke-width="20"
          />
        </SVGIcon>
      </PlayerContainer>
      <Timer size="sm">0:00</Timer>
    </div>
  );
}

export default OceanWave;
